class Solution:
    def twoSum(self, nums: List[int], target: int) -> List[int]:
        ind=[i for i in range(len(nums))]
        temp=sorted(zip(nums,ind))
        nums=[i[0] for i in temp]
        ind=[i[1] for i in temp]
        l,r=0,len(nums)-1
        cal=nums[0]+nums[-1]
        minus=abs(cal-target)
        res=[ind[l],ind[r]]
        while l<r:
            if cal<target:
                l+=1
            elif cal>target:
                r-=1
            else:
                return res
            cal=nums[r]+nums[l]
            if abs(cal-target)<=minus:
                minus=abs(cal-target)

                res=[ind[l],ind[r]]

        return res
